import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Header from './components/Header';
import MinhasTarefas from './pages/MinhasTarefas';
import NovaTarefa from './pages/NovaTarefa';
import EditarTarefa from './pages/EditarTarefa';
import Categorias from './pages/Categorias';
import './App.css';

function AppContent() {
  const location = useLocation();
  const showSearch = location.pathname !== '/nova-tarefa' && !location.pathname.startsWith('/editar-tarefa');

  return (
    <div className="relative flex size-full min-h-screen flex-col bg-slate-50 group/design-root overflow-x-hidden" style={{fontFamily: 'Manrope, "Noto Sans", sans-serif'}}>
      <div className="layout-container flex h-full grow flex-col">
        <Header showSearch={showSearch} />
        <Routes>
          <Route path="/" element={<MinhasTarefas />} />
          <Route path="/minhas-tarefas" element={<MinhasTarefas />} />
          <Route path="/nova-tarefa" element={<NovaTarefa />} />
          <Route path="/editar-tarefa/:id" element={<EditarTarefa />} />
        </Routes>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
