import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const EditarTarefa = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  
  const [formData, setFormData] = useState({
    nomeTarefa: '',
    status: '',
    prioridade: '',
    responsavel: '',
    projeto: '',
    descricao: '',
    dataInicio: '',
    dataConclusao: ''
  });

  const statusOptions = [
    'Pendente',
    'Em andamento',
    'Concluída',
    'Pausada',
    'Cancelada'
  ];

  const prioridadeOptions = [
    'Baixa',
    'Média',
    'Alta',
    'Urgente'
  ];

  const responsavelOptions = [
    'João Silva',
    'Maria Santos',
    'Pedro Oliveira',
    'Ana Costa',
    'Carlos Ferreira',
    'Sofia Rodrigues'
  ];

  const projetoOptions = [
    'Projeto Alpha',
    'Projeto Beta',
    'Projeto Gamma',
    'Website Corporativo',
    'App Mobile',
    'Sistema de Gestão'
  ];

  useEffect(() => {
    if (id) {
      const tarefasExemplo = {
        1: {
          nomeTarefa: 'Estudar Node.js',
          status: 'Pendente',
          prioridade: 'Alta',
          responsavel: 'João Silva',
          projeto: 'Projeto Alpha',
          descricao: 'Estudar conceitos avançados de Node.js para implementação no projeto.',
          dataInicio: '2025-01-15',
          dataConclusao: '2025-05-25'
        },
        2: {
          nomeTarefa: 'Revisar Documentação do Projeto',
          status: 'Em andamento',
          prioridade: 'Média',
          responsavel: 'Maria Santos',
          projeto: 'Projeto Beta',
          descricao: 'Revisar toda a documentação técnica do projeto antes da entrega.',
          dataInicio: '2025-01-10',
          dataConclusao: '2025-05-26'
        },
        3: {
          nomeTarefa: 'Preparar Apresentação para o Cliente',
          status: 'Pendente',
          prioridade: 'Alta',
          responsavel: 'Pedro Oliveira',
          projeto: 'Website Corporativo',
          descricao: 'Criar apresentação executiva para demonstração dos resultados.',
          dataInicio: '2025-01-20',
          dataConclusao: '2025-05-27'
        }
      };

      const tarefaExistente = tarefasExemplo[id];
      if (tarefaExistente) {
        setFormData(tarefaExistente);
      }
    }
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSalvar = (e) => {
    e.preventDefault();
    
    if (!formData.nomeTarefa) {
      alert('Por favor, preencha o nome da tarefa.');
      return;
    }
    
    if (!formData.status) {
      alert('Por favor, selecione um status.');
      return;
    }
    
    if (!formData.prioridade) {
      alert('Por favor, selecione uma prioridade.');
      return;
    }
    
    if (!formData.responsavel) {
      alert('Por favor, selecione um responsável.');
      return;
    }

    console.log('Tarefa editada:', formData);
    alert('Tarefa atualizada com sucesso!');
    
    navigate('/minhas-tarefas');
  };

  return (
    <div className="px-40 flex flex-1 justify-center py-5">
      <div className="layout-content-container flex flex-col w-[512px] max-w-[512px] py-5 max-w-[960px] flex-1">
        <div className="flex flex-wrap justify-between gap-3 p-4">
          <p className="text-[#0d141c] tracking-light text-[32px] font-bold leading-tight min-w-72">Editar Tarefa</p>
        </div>
        
        <form onSubmit={handleSalvar}>
          <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
            <label className="flex flex-col min-w-40 flex-1">
              <p className="text-[#0d141c] text-base font-medium leading-normal pb-2">Nome da Tarefa</p>
              <input
                name="nomeTarefa"
                value={formData.nomeTarefa}
                onChange={handleInputChange}
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#0d141c] focus:outline-0 focus:ring-0 border border-[#cedbe8] bg-slate-50 focus:border-[#cedbe8] h-14 placeholder:text-[#49739c] p-[15px] text-base font-normal leading-normal"
                required
              />
            </label>
          </div>

          <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
            <label className="flex flex-col min-w-40 flex-1">
              <p className="text-[#0d141c] text-base font-medium leading-normal pb-2">Status</p>
              <select
                name="status"
                value={formData.status}
                onChange={handleInputChange}
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#0d141c] focus:outline-0 focus:ring-0 border border-[#cedbe8] bg-slate-50 focus:border-[#cedbe8] h-14 placeholder:text-[#49739c] p-[15px] text-base font-normal leading-normal"
                required
              >
                <option value="">Selecione o status</option>
                {statusOptions.map((status) => (
                  <option key={status} value={status}>
                    {status}
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
            <label className="flex flex-col min-w-40 flex-1">
              <p className="text-[#0d141c] text-base font-medium leading-normal pb-2">Prioridade</p>
              <select
                name="prioridade"
                value={formData.prioridade}
                onChange={handleInputChange}
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#0d141c] focus:outline-0 focus:ring-0 border border-[#cedbe8] bg-slate-50 focus:border-[#cedbe8] h-14 placeholder:text-[#49739c] p-[15px] text-base font-normal leading-normal"
                required
              >
                <option value="">Selecione a prioridade</option>
                {prioridadeOptions.map((prioridade) => (
                  <option key={prioridade} value={prioridade}>
                    {prioridade}
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
            <label className="flex flex-col min-w-40 flex-1">
              <p className="text-[#0d141c] text-base font-medium leading-normal pb-2">Responsável</p>
              <select
                name="responsavel"
                value={formData.responsavel}
                onChange={handleInputChange}
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#0d141c] focus:outline-0 focus:ring-0 border border-[#cedbe8] bg-slate-50 focus:border-[#cedbe8] h-14 placeholder:text-[#49739c] p-[15px] text-base font-normal leading-normal"
                required
              >
                <option value="">Selecione o responsável</option>
                {responsavelOptions.map((responsavel) => (
                  <option key={responsavel} value={responsavel}>
                    {responsavel}
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
            <label className="flex flex-col min-w-40 flex-1">
              <p className="text-[#0d141c] text-base font-medium leading-normal pb-2">Projeto</p>
              <select
                name="projeto"
                value={formData.projeto}
                onChange={handleInputChange}
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#0d141c] focus:outline-0 focus:ring-0 border border-[#cedbe8] bg-slate-50 focus:border-[#cedbe8] h-14 placeholder:text-[#49739c] p-[15px] text-base font-normal leading-normal"
              >
                <option value="">Selecione o projeto</option>
                {projetoOptions.map((projeto) => (
                  <option key={projeto} value={projeto}>
                    {projeto}
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
            <label className="flex flex-col min-w-40 flex-1">
              <p className="text-[#0d141c] text-base font-medium leading-normal pb-2">Descrição</p>
              <textarea
                name="descricao"
                value={formData.descricao}
                onChange={handleInputChange}
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#0d141c] focus:outline-0 focus:ring-0 border border-[#cedbe8] bg-slate-50 focus:border-[#cedbe8] min-h-36 placeholder:text-[#49739c] p-[15px] text-base font-normal leading-normal"
                rows="4"
              />
            </label>
          </div>

          <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
            <label className="flex flex-col min-w-40 flex-1">
              <p className="text-[#0d141c] text-base font-medium leading-normal pb-2">Data de Início</p>
              <input
                name="dataInicio"
                type="date"
                value={formData.dataInicio}
                onChange={handleInputChange}
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#0d141c] focus:outline-0 focus:ring-0 border border-[#cedbe8] bg-slate-50 focus:border-[#cedbe8] h-14 placeholder:text-[#49739c] p-[15px] text-base font-normal leading-normal"
              />
            </label>
            <label className="flex flex-col min-w-40 flex-1">
              <p className="text-[#0d141c] text-base font-medium leading-normal pb-2">Data de Conclusão</p>
              <input
                name="dataConclusao"
                type="date"
                value={formData.dataConclusao}
                onChange={handleInputChange}
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#0d141c] focus:outline-0 focus:ring-0 border border-[#cedbe8] bg-slate-50 focus:border-[#cedbe8] h-14 placeholder:text-[#49739c] p-[15px] text-base font-normal leading-normal"
              />
            </label>
          </div>

          <div className="flex px-4 py-3 justify-end">
            <button
              type="submit"
              className="flex min-w-[84px] max-w-[480px] cursor-pointer items-center justify-center overflow-hidden rounded-lg h-10 px-4 bg-[#0c7ff2] text-slate-50 text-sm font-bold leading-normal tracking-[0.015em] hover:bg-[#0a6cd9] transition-colors"
            >
              <span className="truncate">Salvar</span>
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditarTarefa;
