import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Categorias = () => {
  const navigate = useNavigate();
  
  const [nomeCategoria, setNomeCategoria] = useState('');
  const [categorias, setCategorias] = useState([
    { id: 1, nome: 'Estudos' },
    { id: 2, nome: 'Trabalho' },
    { id: 3, nome: 'Pessoal' }
  ]);

  const handleCriarCategoria = (e) => {
    e.preventDefault();
    
    if (!nomeCategoria.trim()) {
      alert('Por favor, digite o nome da categoria.');
      return;
    }
    
    if (categorias.some(cat => cat.nome.toLowerCase() === nomeCategoria.toLowerCase())) {
      alert('Esta categoria já existe.');
      return;
    }

    const novaCategoria = {
      id: categorias.length + 1,
      nome: nomeCategoria.trim()
    };

    setCategorias(prev => [...prev, novaCategoria]);
    setNomeCategoria('');
    alert('Categoria criada com sucesso!');
  };

  const handleEditarCategoria = (id) => {
    const categoria = categorias.find(cat => cat.id === id);
    const novoNome = prompt('Digite o novo nome da categoria:', categoria.nome);
    
    if (novoNome && novoNome.trim() && novoNome !== categoria.nome) {
      if (categorias.some(cat => cat.nome.toLowerCase() === novoNome.toLowerCase() && cat.id !== id)) {
        alert('Já existe uma categoria com este nome.');
        return;
      }
      
      setCategorias(prev => 
        prev.map(cat => 
          cat.id === id ? { ...cat, nome: novoNome.trim() } : cat
        )
      );
      alert('Categoria atualizada com sucesso!');
    }
  };

  const handleExcluirCategoria = (id) => {
    if (window.confirm('Tem certeza que deseja excluir esta categoria?')) {
      setCategorias(prev => prev.filter(cat => cat.id !== id));
      alert('Categoria excluída com sucesso!');
    }
  };

  return (
    <div className="px-40 flex flex-1 justify-center py-5">
      <div className="layout-content-container flex flex-col w-[512px] max-w-[512px] py-5 max-w-[960px] flex-1">
        <div className="flex flex-wrap justify-between gap-3 p-4">
          <p className="text-[#0d141c] tracking-light text-[32px] font-bold leading-tight min-w-72">Categorias</p>
        </div>
        
        <form onSubmit={handleCriarCategoria}>
          <div className="flex max-w-[480px] flex-wrap items-end gap-4 px-4 py-3">
            <label className="flex flex-col min-w-40 flex-1">
              <p className="text-[#0d141c] text-base font-medium leading-normal pb-2">Nome da Categoria</p>
              <input
                value={nomeCategoria}
                onChange={(e) => setNomeCategoria(e.target.value)}
                placeholder="Digite o nome da categoria"
                className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-[#0d141c] focus:outline-0 focus:ring-0 border border-[#cedbe8] bg-slate-50 focus:border-[#cedbe8] h-14 placeholder:text-[#49739c] p-[15px] text-base font-normal leading-normal"
                required
              />
            </label>
          </div>
          
          <div className="flex px-4 py-3 justify-end">
            <button
              type="submit"
              className="flex min-w-[84px] max-w-[480px] cursor-pointer items-center justify-center overflow-hidden rounded-lg h-10 px-4 bg-[#0c7ff2] text-slate-50 text-sm font-bold leading-normal tracking-[0.015em] hover:bg-[#0a6cd9] transition-colors"
            >
              <span className="truncate">Criar Categoria</span>
            </button>
          </div>
        </form>

        <h3 className="text-[#0d141c] text-lg font-bold leading-tight tracking-[-0.015em] px-4 pb-2 pt-4">Minhas Categorias</h3>
        
        <div className="flex flex-col">
          {categorias.length === 0 ? (
            <div className="flex items-center gap-4 bg-slate-50 px-4 min-h-14 justify-center">
              <p className="text-[#49739c] text-base font-normal leading-normal">Nenhuma categoria encontrada.</p>
            </div>
          ) : (
            categorias.map((categoria) => (
              <div key={categoria.id} className="flex items-center gap-4 bg-slate-50 px-4 min-h-14 justify-between border-b border-[#e7edf4] hover:bg-[#f1f5f9] transition-colors">
                <p className="text-[#0d141c] text-base font-normal leading-normal flex-1 truncate">{categoria.nome}</p>
                <div className="shrink-0 flex gap-2">
                  <button 
                    onClick={() => handleEditarCategoria(categoria.id)}
                    className="text-base font-medium leading-normal text-[#0c7ff2] hover:text-[#0a6cd9] transition-colors"
                  >
                    Editar
                  </button>
                  <button 
                    onClick={() => handleExcluirCategoria(categoria.id)}
                    className="text-base font-medium leading-normal text-[#dc2626] hover:text-[#b91c1c] transition-colors"
                  >
                    Excluir
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Categorias;
